mapper.select:
SELECT t.id, t.project, t.type, a.version, a.remark, t.uid, a.id as versionId FROM at_project t join at_project_host a on t.id = a.project_id WHERE 1=1 AND t.status = 0 AND a.status = 0
AND t.uid = #{uid}
AND 1=2
AND t.project LIKE CONCAT('%',#{key_word},'%')
ORDER BY a.update_time desc limit #{startRow},#{pageSize}

SELECT t.id, t.project, t.type, t.uid FROM at_project t WHERE 1=1 AND t.status = 0
AND t.uid = #{uid}
AND 1=2

mapper.update:
update at_project t set

    t.type = #{type}

where 1=1 and t.id = #{id} and t.uid = #{uid};

update at_project_host a set
a.version = #{version},
a.remark = #{desc}
where 1=1
and a.project_id = #{projectId}


        AND a.id = #{versionId}


        AND 1=2

;

update at_project t set t.status = 1
where 1 = 1


        AND t.uid = #{uid}


        AND 1=2


and t.id in

    #{id}

update at_project_host t set t.status = 1 where 1 = 1
AND t.id = #{id}
AND 1=2
