mapper.select:
SELECT aph.id, ap.id as projectId, ap.project, aph.version, aph.domain, aph.ip FROM at_project ap LEFT JOIN at_project_host aph ON ap.id = aph.project_id WHERE 1=1 AND ap.status = 0 AND aph.status = 0
AND ap.uid = #{uid}
AND 1=2
ORDER BY ap.project, aph.version;

SELECT uid FROM at_project ap WHERE 1=1
AND ap.id = #{projectId}
AND 1=2

SELECT t.version
FROM
at_project_host t JOIN at_project a ON t.project_id = a.id
WHERE 1=1


        AND a.uid = #{uid}


        AND 1=2




        AND a.id = #{projectId}


        AND 1=2




        AND t.version = #{version}


        AND 1=2

mapper.update:
update at_project_host t
set t.ip = #{hostIp},
     t.domain = #{domain}
where t.id = #{hostId}
and t.project_id = #{projectId}

mapper.select:
SELECT t.id, t.project_id, t.module_id, t.version, t.api_name, t.protocol_type, t.method_type, t.domain, t.uri, t.status, a.project, b.module, a.update_time
FROM at_project_api t LEFT JOIN at_project a ON t.project_id = a.id
LEFT JOIN at_project_module b ON t.module_id = b.id
WHERE 1=1
AND t.status != -1


        AND a.uid = #{uid}


        AND 1=2



    AND t.api_name LIKE CONCAT('%',#{key_word},'%')



        AND ( t.module_id in

            #{id}

        OR t.project_id in

            #{id}

        )


        AND 1=2



    ORDER BY t.update_time desc limit #{startRow},#{pageSize}

SELECT count(1) FROM at_project_api t left join at_project a on t.project_id = a.id WHERE 1=1 AND t.status != -1
AND a.uid = #{uid}
AND 1=2
AND t.api_name LIKE CONCAT('%',#{key_word},'%')
AND ( t.module_id in
#{id}
OR t.project_id in
#{id}
)
AND 1=2

SELECT t.id
FROM
at_project_api t JOIN at_project a ON t.project_id = a.id
WHERE 1=1


        AND a.uid = #{uid}


        AND 1=2




        AND a.id = #{projectId}


        AND 1=2




        AND t.id = #{apiId}


        AND 1=2

mapper.update:
update at_project_api t
set t.method_type = #{apiMethodType}

        ,t.module_id = #{moduleId}


        ,t.protocol_type = #{apiProtoType}


        ,t.method_type = #{apiMethodType}


        ,t.domain = #{domain}


        ,t.uri = #{uri}


        ,t.api_name = #{apiName}

where t.id = #{id}
and t.project_id = #{projectId}

update at_project_api t
left join at_project a on t.project_id = a.id
set t.status = #{status}
where 1 = 1


        AND a.uid = #{uid}


        AND 1=2


and t.id in

    #{id}

mapper.select:
SELECT t.id, t.project_id, t.api_name, t.protocol_type, t.method_type, t.domain, t.uri FROM at_project_api t JOIN at_project a ON t.project_id = a.id WHERE 1=1
AND a.uid = #{uid}
AND 1=2
AND t.id = #{apiId}
AND 1=2

SELECT t.id, t.api_id, t.param_type, t.request_key, t.request_value, t.is_correlation, t.status FROM at_api_info t WHERE 1=1 AND t.status = 0
AND t.api_id = #{apiId}
AND 1=2

mapper.delete:
DELETE FROM at_api_info WHERE 1=1
AND api_id = #{apiId}
AND 1=2
